Json::Value Book::toJson() const
{
    Json::Value ret;
    if(getBookId())
    {
        ret["Book_ID"]=getValueOfBookId();
    }
    else
    {
        ret["Book_ID"]=Json::Value();
    }
    if(getName())
    {
        ret["Name"]=getValueOfName();
    }
    else
    {
        ret["Name"]=Json::Value();
    }
    if(getStatus())
    {
        ret["Status"]=getValueOfStatus();
    }
    else
    {
        ret["Status"]=Json::Value();
    }
    if(getSynopsis())
    {
        ret["Synopsis"]=getValueOfSynopsis();
    }
    else
    {
        ret["Synopsis"]=Json::Value();
    }
    if(getPublisher())
    {
        ret["Publisher"]=getValueOfPublisher();
    }
    else
    {
        ret["Publisher"]=Json::Value();
    }
    if(getAuthor())
    {
        ret["Author"]=getValueOfAuthor();
    }
    else
    {
        ret["Author"]=Json::Value();
    }
    if(getCreate())
    {
        ret["Create"]=getCreate()->toDbStringLocal();
    }
    else
    {
        ret["Create"]=Json::Value();
    }
    if(getUpdate())
    {
        ret["Update"]=getUpdate()->toDbStringLocal();
    }
    else
    {
        ret["Update"]=Json::Value();
    }
    return ret;
}

